CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC" (
    "CREATETIMESTAMP" TIMESTAMP NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "AUTHENTICATIONMETHOD" VARCHAR(4096) NOT NULL,
    "CONTEXT" VARCHAR(4096) NOT NULL,
    "USER" VARCHAR(4096) NOT NULL,
);


CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC_STEP" (
    "CREATETIMESTAMP" TIMESTAMP NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "DIAGNOSTICID" BIGINT NOT NULL,
    "NUMBER" BIGINT NOT NULL,
    "URL" VARCHAR(4096) NOT NULL,
    "DESCRIPTION" VARCHAR(4096) NOT NULL,
    "WEBELEMENTID" BIGINT NULL,
);

CREATE INDEX "AUTHHELPER_DIAGNOSTIC_STEP_DIAGNOSTICID_IDX" ON "AUTHHELPER_DIAGNOSTIC_STEP" ("DIAGNOSTICID");


CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC_MESSAGE" (
    "CREATETIMESTAMP" TIMESTAMP NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "STEPID" BIGINT NOT NULL,
    "NUMBER" BIGINT NOT NULL,
    "MESSAGEID" BIGINT NOT NULL,
);

CREATE INDEX "AUTHHELPER_DIAGNOSTIC_MESSAGE_STEPID_IDX" ON "AUTHHELPER_DIAGNOSTIC_MESSAGE" ("STEPID");


CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC_SCREENSHOT" (
    "CREATETIMESTAMP" TIMESTAMP NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "STEPID" BIGINT NOT NULL,
    "DATA" VARCHAR(8388608) NOT NULL,
);

CREATE INDEX "AUTHHELPER_DIAGNOSTIC_SCREENSHOT_STEPID_IDX" ON "AUTHHELPER_DIAGNOSTIC_SCREENSHOT" ("STEPID");


CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC_WEB_ELEMENT" (
    "CREATETIMESTAMP" TIMESTAMP NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "FORMINDEX" BIGINT NULL,
    "ATTRIBUTETYPE" VARCHAR(1024) NULL,
    "ATTRIBUTEID" VARCHAR(1024) NULL,
    "ATTRIBUTENAME" VARCHAR(1024) NULL,
    "ATTRIBUTEVALUE" VARCHAR(1024) NULL,
    "TEXT" VARCHAR(4096) NULL,
    "DISPLAYED" BOOLEAN NOT NULL,
    "ENABLED" BOOLEAN NOT NULL,
);


CREATE CACHED TABLE IF NOT EXISTS "AUTHHELPER_DIAGNOSTIC_STEP_WEB_ELEMENTS" (
    "STEPID" BIGINT NOT NULL,
    "WEBELEMENTID" BIGINT NOT NULL,
    "NUMBER" BIGINT NOT NULL,
);

CREATE INDEX "AUTHHELPER_DIAGNOSTIC_STEP_WEB_ELEMENTS_STEPID_IDX" ON "AUTHHELPER_DIAGNOSTIC_STEP_WEB_ELEMENTS" ("STEPID");
CREATE INDEX "AUTHHELPER_DIAGNOSTIC_STEP_WEB_ELEMENTS_WEBELEMENTID_IDX" ON "AUTHHELPER_DIAGNOSTIC_STEP_WEB_ELEMENTS" ("WEBELEMENTID");
